mapper.select:
select count(1) as count, from_id as fromId from im_message_data imd


        AND imd.owner_id = #{ownerId}



        AND imd.from_id  <>   #{ownerId}



        AND imd.send_status = #{sendStatus}


group by imd.from_id

select t1.*, u.nick_name, u.account, u.photo, if2.friend_nick_name from im_message_data t1 join ( select MAX(message_time) as message_time from im_message_data
AND owner_id = #{ownerId}
AND from_id <> #{ownerId}
group by from_id ) t2 on t1.message_time = t2.message_time join im_friendship if2 on t1.from_id = if2.to_id and t1.to_id = if2.from_id join im_user_data u on t1.from_id = u.user_id
AND t1.owner_id = #{ownerId}
AND t1.from_id <> #{ownerId}
and t1.from_type = 1
union select t1.*, u.group_name as nick_name, u.group_id as account, u.photo, u.group_name as friend_nick_name from im_message_data t1 join ( select MAX(message_time) as message_time from im_message_data
AND owner_id = #{ownerId}
AND from_id <> #{ownerId}
and from_type = 3
group by from_id ) t2 on t1.message_time = t2.message_time join im_group_member if2 on t1.from_id = if2.group_id and t1.to_id = if2.member_id join im_group u on t1.from_id = u.group_id
AND t1.owner_id = #{ownerId}
AND t1.from_id <> #{ownerId}
and t1.from_type = 3
